
//Find the greatest product of five consecutive digits in the 1000-digit number.


public class Problem8 {
	public static void main(String args[]) {
		String num = "";
		int start = 0;
		int max = 0;
		int sum = 0;
		int first, second, third, fourth, fifth;
		while (start+4 < num.length()) {	
			first = Integer.parseInt(num.substring(start,start+1));
			second = Integer.parseInt(num.substring(start+1, start+2));
			third = Integer.parseInt(num.substring(start+2, start+3));
			fourth = Integer.parseInt(num.substring(start+3, start+4));
			fifth = Integer.parseInt(num.substring(start+4, start+5));
			sum = first*second*third*fourth*fifth;
			if (sum > max) {
				max = sum;
			}
			start++;
		}
		System.out.println(max);
	}
}
